<script setup lang="ts">
import { onBeforeUnmount, onMounted, ref, h, onUpdated } from 'vue';

import { GFXComponent, GFXLayers } from './types/gfxcomponent';

import GFXController from './handlers/controller';
import { ShortcutHotKeys } from './tools/shortcuts';

import Clock from './elements/Clock.vue';
import Formation from './elements/Formation.vue';
import Ident from './elements/Ident.vue';
import Strap from './elements/Strap.vue';
import Standings from './elements/Standings.vue';
import Promo from './elements/Promo.vue';
import ManOfTheMatch from './elements/ManOfTheMatch.vue';
import MatchWinner from './elements/MatchWinner.vue';

const params = new URLSearchParams(window.location.search);
let devMode = params.get('dev') != null;
const league = params.get('league') || 'mlsnp';

const numberOfLayers = 30;
const template: GFXLayers = [];
for (let i = 1; i <= numberOfLayers; i++) {
  template.push({
    id: i,
    name: 'Unassigned',
    ref: ref<GFXComponent | undefined>(),
  });
}

let controller: GFXController = new GFXController(template);
let shortcuts: ShortcutHotKeys | null = devMode ? new ShortcutHotKeys(template) : null;

const ctrlUrl = params.get('controller');
if (ctrlUrl) {
  controller.connect(ctrlUrl);
}

onMounted(() => {
  // window.addEventListener('keydown', DevShortcuts)
  if (devMode && shortcuts) {
    shortcuts.start();

    document.body.classList.add('development');
  }

  import.meta.hot?.on('vite:afterUpdate', () => {
    controller?.refresh();
    console.log('HOT RELOAD UPDATED');
  });
});

onBeforeUnmount(() => {
  if (controller) controller.close();
  if (devMode && shortcuts) {
    shortcuts.stop();
  }
  // window.removeEventListener('keydown',  DevShortcuts)
});

document.documentElement.setAttribute('data-theme', league);
</script>

<template>
  <div class="viewpoint">
    <Clock
      :league="league"
      :ref="template[0].ref"
    />
    <Ident
      :league="league"
      :ref="template[1].ref"
    />
    <Strap :ref="template[2].ref" />
    <Standings :ref="template[3].ref" />
    <Formation
      :league="league"
      :ref="template[6].ref"
    />
    <Formation
      :league="league"
      :ref="template[7].ref"
    />
    <Promo :ref="template[8].ref" />
    <MatchWinner :ref="template[9].ref" />
    <ManOfTheMatch :ref="template[10].ref" />
    <!-- <BoilerPlate :settings="{}" :ref="template[2].ref"/> -->
  </div>
  <component
    v-if="devMode"
    :is="shortcuts?.render()"
  ></component>
</template>

<style lang="scss">
body {
  // background: #242424;
  overflow: hidden;
}

.development {
  background-color: var(--color-600);
  // background: #00ff00;

  .viewpoint {
    // border: 1px solid #eee;
    background-image: url('/ucla.jpg');
    // margin-top: 300px;
    // background-image: url("/clock.png");
  }
}

.viewpoint {
  overflow: hidden;
  position: absolute;
  top: 0;
  left: 0;
  width: 1920px;
  height: 1080px;
  border: 1px solid var(--color-700);
}
</style>
