```vue nep-no-mlsnp-graphics/src/components/clock-team.vue
<script setup lang="ts">
  import { onMounted, ref } from 'vue';
  import textElm from '../components/clock-text.vue'

  const props = defineProps<{
    timeline: gsap.core.Timeline;
    title: string,
    team: string,
    teamcolor: string,
    position: string
  }>()

  const colorLineHome = ref<HTMLDivElement>()
  const colorLineAway = ref<HTMLDivElement>()
  const teamWrapper = ref<HTMLDivElement>()
  const teamname = ref<HTMLDivElement>()

  onMounted(() => {
    // Not pretty, but it works
    props.timeline.addLabel("start")
    if(teamname.value && props.position == "home")
      props.timeline.from(teamname.value, {right: "-48px", duration: 0.6}, '<')
    if(teamname.value && props.position == "away")
      props.timeline.from(teamname.value, {left: "-48px", duration: 0.6}, '<')
    if(teamWrapper.value)
      props.timeline.fromTo(teamWrapper.value, {opacity: "0"}, {opacity: "1", duration: 0.6}, '<')
    if(colorLineHome.value)
      props.timeline.from(colorLineHome.value, {left: "-12px", duration: 0.4}, '<0.25')
    if(colorLineAway.value)
      props.timeline.from(colorLineAway.value, {right: "-12px", duration: 0.4}, '<0.25')
    props.timeline.addLabel("stop")
    // TODO: Fiks animasjon ut, funker ikke???
    // if(teamWrapper.value)
    //   props.timeline.fromTo(teamWrapper.value, {opacity: "1"}, {opacity: "0", duration: 0.4}, '<')
    props.timeline.addLabel("end")
  })
</script>

