  redcardHome2_timeline,
  redcardHome3_timeline,
  redcardAway1_timeline,
  redcardAway2_timeline,
  redcardAway3_timeline,
  overtime_timeline,
  referee_timeline,
  substitute_timeline,
  yellowcard_timeline,
  redcard_timeline,
  goal_timeline
}, state)

//@ts-ignore
window.clockstore = store //DEBUGGING
//@ts-ignore
window.clocktl = tl //DEBUGGING

function animateInRedCards() {
  switch (store.value.fields.homeredcards) {
    default:
    case 3:
      redcardHome3_timeline.tweenFromTo("start", "stop")
    case 2:
      redcardHome2_timeline.tweenFromTo("start", "stop")
    case 1:
      redcardHome1_timeline.tweenFromTo("start", "stop")
    case 0:
      break;
  }

  switch (store.value.fields.awayredcards) {
    default:
    case 3:
      redcardAway3_timeline.tweenFromTo("start", "stop")
    case 2:
      redcardAway2_timeline.tweenFromTo("start", "stop")
    case 1:
      redcardAway1_timeline.tweenFromTo("start", "stop")
    case 0:
      break;
  }
}

function animateOutRedCards() {
  switch (store.value.fields.homeredcards) {
    default:
    case 3:
      redcardHome3_timeline.tweenFromTo("stop", "end")
    case 2:
      redcardHome2_timeline.tweenFromTo("stop", "end")
    case 1:
      redcardHome1_timeline.tweenFromTo("stop", "end")
    case 0:
      break;
  }

  switch (store.value.fields.awayredcards) {
    default:
    case 3:
      redcardAway3_timeline.tweenFromTo("stop", "end")
    case 2:
      redcardAway2_timeline.tweenFromTo("stop", "end")
    case 1:
      redcardAway1_timeline.tweenFromTo("stop", "end")
    case 0:
      break;
  }
}

function animateInComponents() {
  league_timeline.tweenFromTo("start", "stop")
  hometeam_timeline.tweenFromTo("start", "stop")
  awayteam_timeline.tweenFromTo("start", "stop")
  scorehome_timeline.tweenFromTo("start", "stop")
  scoreaway_timeline.tweenFromTo("start", "stop")
  time_timeline.tweenFromTo("start", "stop")
}

function animateOutComponents() {
  league_timeline.tweenFromTo("stop", "end")
  hometeam_timeline.tweenFromTo("stop", "end")
  awayteam_timeline.tweenFromTo("stop", "end")
  scorehome_timeline.tweenFromTo("stop", "end")
  scoreaway_timeline.tweenFromTo("stop", "end")
  time_timeline.tweenFromTo("stop", "end")
}

function runAnimationIN() {
  state.value = "IN"
  wptl.restart()
  bordertl.restart()
  tl.tweenFromTo("start", "stop")
}

function runAnimationOUT() {
  state.value = "OUT"
  tl.tweenFromTo("stop", "end")
}

function runAnimationNEXT() {
  state.value = "NEXT"
}

defineProps<{
  league: string
}>()

defineExpose({
  name: "Clock",
  runAnimationIN,
  runAnimationOUT,
  runAnimationNEXT,
  state,
  store
})

onMounted(() => {
  setInterval(store.value.commands.tick, 250)
  if (clock.value && watermarkElm.value)
    tl
      .addLabel("start")
      // Clock
      .to(clock.value, {
        opacity: 1, duration: 0.75, ease: 'power2.inOut', onStart: () => {
          animateInComponents()
        }, onComplete: () => {
          animateInRedCards()
        }
      }, '0.1')
      // Watermark
      .to(watermarkElm.value, {opacity: 1, duration: 0.75, ease: 'power2.inOut'}, '<')


      .addLabel("stop")
      .to(clock.value, {opacity: 0, duration: 0.75, ease: 'power2.inOut', onStart: () => {
          animateOutRedCards()
          animateOutComponents()
        }
      })

      // Watermark
      .to(watermarkElm.value, {opacity: 0, duration: 1, ease: 'power2.inOut'}, '<')
      .addLabel("end")

  if (wallpaper.value && wallpaperItem1.value && wallpaperItem2.value)
    wptl
      .addLabel("start")
      .to(wallpaper.value, { opacity: 1, duration: 0.5, ease: 'power2.inOut' })
      .to(wallpaperItem1.value, { opacity: 0.8, duration: 0.5, ease: 'power2.inOut' }, '<')
      .from(wallpaperItem1.value, { left: "-100%", duration: 0.5, ease: 'power2.inOut' }, '<')
      .from(wallpaperItem1.value, { bottom: "-150%", duration: 0.5, ease: 'power2.inOut' }, '<')
      .to(wallpaperItem2.value, { opacity: 1, duration: 0.5, ease: 'power2.inOut' }, '<25%')
      .to(wallpaper.value, { opacity: 0, duration: 0.75, ease: 'power2.inOut', delay: 5 })
      .addLabel("stop")
      .to(wallpaper.value, { opacity: 0, duration: 0.75, ease: 'power2.inOut' })
      .addLabel("end")
  if (border.value)
    bordertl
      .addLabel("start")
      .to(border.value, {opacity: 1, duration: 0.75, ease: 'power2.inOut'}, '<')
      .fromTo(border.value, {width: "0px"}, {width: "528px", duration: 0.75, ease: 'power2.inOut'}, '<')
      .to(border.value, {opacity: 0, duration: 0.5, ease: 'power2.inOut'}, '>')
      .addLabel("stop")
})
/*
  Animasjon:
  - wallpaper
    - inn
      - x mørkeblå radial gradeint som beveger seg fra bottom left til top right langs den skrå streken
      - x resten bare fade inn
    - ut
      - x fade ut
  - liga logo
    - inn
      - x fade og zoom
    - ut
      - x fade
  - clock
    - inn
      -X wrapper fade inn
      -X border bottom blå gradeient som går fra venstre til høyre, men fader ut etter ~65% av veien til høyre
      - x lagfarger fra 0 width til 100%?
      - x lagnavn fra center til høyre/venstre og fade
      - / score zoom inn og fade, ingen zoom ble bare stygt
      - tid
        - x bakgrunn fade
        - x tekst fade og zoom
    - ut
      - all tekst fade ut
      - tid bakgrunn fade gradeitn venstre til høyre
      - x wrapper fade
*/
</script>

<template>
  <div class="outside">
    <div class="watermark" v-if="watermark" ref="watermarkElm">
      <img :src="`./logos/league/${league}/watermark.png`" />
    </div>
    <div class="wallpaper" ref="wallpaper">
      <div class="wallpaper-item1" ref="wallpaperItem1"></div>
      <div class="wallpaper-item2" ref="wallpaperItem2"></div>
