```vue nep-no-mlsnp-graphics/src/elements/Strap.vue
<script setup lang="ts">
import { ref, onMounted } from 'vue';
import { AnimationDirection } from '../types/gfxcontroller';
import createStore from '../stores/strap';

import gsap from 'gsap';

const store = createStore();
const state = ref<AnimationDirection>('OUT');

const strap = ref<HTMLElement>();
const awayTeamLogo = ref<HTMLElement>();
const homeTeamLogo = ref<HTMLElement>();
const awayTeamLogoImg = ref<HTMLElement>();
const homeTeamLogoImg = ref<HTMLElement>();
const awayTeamLogoAlt = ref<HTMLElement>();
const homeTeamLogoAlt = ref<HTMLElement>();
const topLine = ref<HTMLElement>();
const dropLine = ref<HTMLElement>();

function runAnimationIN() {
  console.log('Running');
  state.value = 'IN';
  // tl.play()
  tl.tweenFromTo('start', 'stop');
}

function runAnimationOUT() {
  console.log('Stopping');
  state.value = 'OUT';
  tl.tweenFromTo('stop', 'end');
}

function runAnimationNEXT() {
  console.log('Next');
  state.value = 'NEXT';
}

defineProps<{
  league: string;
}>();

defineExpose({
  name: 'Strap',
  runAnimationIN,
  runAnimationOUT,
  runAnimationNEXT,
  state,
  store,
});

let tl = gsap.timeline({ paused: true });

onMounted(() => {
  if (
    !homeTeamLogo.value ||
    !awayTeamLogo.value ||
    !awayTeamLogoAlt.value ||
    !awayTeamLogoImg.value ||
    !homeTeamLogoImg.value ||
    !homeTeamLogoAlt.value ||
    !dropLine.value ||
    !topLine.value ||
    !strap.value
  ) {
    return;
  }

  tl.addLabel('start')
    .fromTo(
      strap.value,
      { opacity: 0 },
      {
        opacity: 1,
        duration: 0.04,
      }
    )
    .to(awayTeamLogoAlt.value, { scale: 10, duration: 0.001 }, 'start')
    .to(homeTeamLogoAlt.value, { scale: 10, duration: 0.001 }, 'start')
    .to(awayTeamLogoImg.value, { opacity: 0 }, 'start')
    .to(homeTeamLogoImg.value, { opacity: 0 }, 'start')
    .fromTo(awayTeamLogoAlt.value, { scale: 10 }, { scale: 5, duration: 0.1, ease: 'power2.inOut' }, 'start+=.1')
    .fromTo(awayTeamLogoAlt.value, { scale: 5 }, { scale: 3, duration: 0.1, ease: 'power2.inOut' }, 'start+=.3')
    .fromTo(awayTeamLogoAlt.value, { opacity: 1, scale: 3 }, { opacity: 0, scale: 1, duration: 0.1, ease: 'power2.inOut' }, 'start+=.6')
    .from(awayTeamLogoImg.value, { opacity: 0, duration: 0.1 }, 'start+=.6')
    .fromTo(homeTeamLogoAlt.value, { scale: 10 }, { scale: 5, duration: 0.1, ease: 'power2.inOut' }, 'start+=.1')
    .fromTo(homeTeamLogoAlt.value, { scale: 5 }, { scale: 3, duration: 0.1, ease: 'power2.inOut' }, 'start+=.3')
    .fromTo(homeTeamLogoAlt.value, { opacity: 1, scale: 3 }, { opacity: 0, scale: 1, duration: 0.1, ease: 'power2.inOut' }, 'start+=.6')
    .from(homeTeamLogoImg.value, { opacity: 0, duration: 0.1 }, 'start+=.6')
    .addLabel('logos')
    .to(homeTeamLogo.value, { width: '8.67%', ease: 'power2.inOut' }, 'logos')
    .to(awayTeamLogo.value, { width: '8.67%', ease: 'power2.inOut' }, 'logos')
    .addLabel('popUpAndDown')
    .to(topLine.value, { y: 50, duration: 0.001 }, 'popUpAndDown')
    .fromTo(topLine.value, { opacity: 0 }, { opacity: 1, duration: 0.02 }, 'popUpAndDown')
    .from(topLine.value, { y: 50, ease: 'power2.inOut' }, 'popUpAndDown+=.3')
    .to(dropLine.value, { y: -50, duration: 0.001 }, 'popUpAndDown')
    .fromTo(dropLine.value, { opacity: 0 }, { opacity: 1, duration: 0.02 }, 'popUpAndDown')
    .from(dropLine.value, { y: -50, ease: 'power2.inOut' }, 'popUpAndDown+=1')
    .addLabel('stop')
    .fromTo(topLine.value, { y: 0 }, { y: 50, duration: 0.4 }, 'stop')
    .fromTo(topLine.value, { opacity: 0 }, { opacity: 1, duration: 0.02 }, 'stop+=.4')
    .fromTo(dropLine.value, { y: 0 }, { y: -50, duration: 0.4 }, 'stop')
    .fromTo(dropLine.value, { opacity: 0 }, { opacity: 1, duration: 0.02 }, 'stop+=.4')
    .fromTo(strap.value, { clipPath: 'inset(0px 0px 0px 0px)' }, { clipPath: 'inset(0px 50%)' }, 'stop+=.5')
    .addLabel('end');
});
</script>

