```ts nep-no-mlsnp-graphics/src/handlers/controller.ts
import { IMessageEvent, w3cwebsocket } from 'websocket';
import { AnimationDirection, GFXPayload, RawGFXPayload } from '../types/gfxcontroller';

import { GFXLayers } from '../types/gfxcomponent';



class GFXController {
  private mode: 'PROD' | 'DEV' = "PROD"
  private url: string = ""
  private socket: w3cwebsocket | null = null
  private template: GFXLayers = []

  constructor(template: GFXLayers, mode: 'PROD' | 'DEV' = 'PROD') {
    this.mode = mode
    this.template = template;
    (window as Window & typeof globalThis & { emulate(data:RawGFXPayload): void }).emulate = (data) => this.emualte(data)
  }

  public connect(url: string) {
    this.url = url
    if (!this.socket) {
      this.socket = new w3cwebsocket(this.url)

      this.socket.onmessage = (msg) => this.executeCommand(msg)

      this.socket.onopen = () => {
        this.socket?.send("newSurface")
        // console.log("CONNECTION OPEN")
      }
    }

  }

  public refresh() {
    this.socket?.send("newSurface")
  }

  public emualte(data: RawGFXPayload): void {
    if(this.mode == 'DEV') data.payload.layer = "1"
    try {
      this.executeCommand({data: JSON.stringify(data)})
    } catch(e) {
      console.error("Could not convert data:", e)
    }
  }

  public useTemplate(template: GFXLayers, mode: 'PROD' | 'DEV'): void {
    this.mode = mode
    this.template = template
  }

  public getSocket(): w3cwebsocket | null {
    return this.socket
  }

  public close() {
    if (this.socket) {
      this.socket.close()
    }
  }


  private animate(index: number, type: AnimationDirection) {
    const layer = this.template[index].ref.value
    console.log(this.template[index].name, type)
    if (layer) {
      switch(type) {
        case 'IN':
          if(layer.state != 'IN') layer.runAnimationIN()
          break;

        case 'OUT':
          if(layer.state != 'OUT') layer.runAnimationOUT()
          break;

        case 'NEXT':
          layer.runAnimationNEXT()
          break;
      }
    }
  }

  public executeCommand(msg: IMessageEvent): void {
    let payload: GFXPayload | null = null
    try {
      payload = JSON.parse((msg.data as string)) as GFXPayload
      // console.log("EXEC:", payload)
      const index = (parseInt(payload.layer) | 0) - 1
      const command = payload.nepcmd ? payload.nepcmd : payload.spxcmd ? payload.spxcmd : ""
      const data = payload.data?.fields

      if (index >= 0 && this.template.length > index) {
        const layer = this.template[index]
        if (!layer.ref.value) return

        const storage = layer.ref.value.store

        // console.log("Layer:", layer)
        // console.log("Storage:", storage)

        if(storage && data) {
          const fields = Object.keys(storage.fields)
          const commands = Object.keys(storage.commands)

          if (this.mode == "DEV") {
            // console.log("FIELDS", fields)
            // console.log("COMMANDS", commands)
          }

          // Fields
          for (let key of fields) {
            if (data[key] != undefined) {
              // console.log(key, storage.fields[key])
              storage.fields[key] = data[key]
            }
          }


          //Commands
          for (let key of commands) {
            if (data[key] != undefined) {
              try {
                const cb = storage.commands[key]
                cb(data[key])
              } catch (e) {
                console.error("Command Update Error:", e)
              }
            }
          }
        }

        // Animation
        switch (command) {
          case 'play':
          case 'playTemplate':
            this.animate(index, 'IN')
            break;

          case 'stop':
          case 'stopTemplate':
            this.animate(index, 'OUT')
            break;

          case 'next':
          case 'nextTemplate':
            this.animate(index, 'NEXT')
            break;

          case 'update':
          case 'updateFunction':
            break;
        }
      }


      // console.log("Command", command)

    } catch (e) {
      console.error(e)
    }
  };
}

export default GFXController
