```ts nep-no-mlsnp-graphics/src/stores/clock.ts
import { Ref, ref } from 'vue'
import { AnimationDirection } from '../types/gfxcontroller'

const ROUNDTIME = 45

export default (timelines: {
  redcardHome1_timeline: gsap.core.Timeline,
  redcardHome2_timeline: gsap.core.Timeline,
  redcardHome3_timeline: gsap.core.Timeline,
  redcardAway1_timeline: gsap.core.Timeline,
  redcardAway2_timeline: gsap.core.Timeline,
  redcardAway3_timeline: gsap.core.Timeline,
  overtime_timeline: gsap.core.Timeline,
  referee_timeline: gsap.core.Timeline,
  substitute_timeline: gsap.core.Timeline,
  yellowcard_timeline: gsap.core.Timeline,
  redcard_timeline: gsap.core.Timeline,
  goal_timeline: gsap.core.Timeline,
}, state: Ref<AnimationDirection>) => {
  const store = ref({
    fields: {
      "hometeam-name": "",
      "awayteam-name": "",
      "referee-name": "",
      "referee-club": "",
      "sub-in-num": "",
      "sub-in-name": "",
      "sub-out-num": "",
      "sub-out-name": "",
      "redcard-name": "",
      "redcard-number": "",
      "yellowcard-name": "",
      "yellowcard-number": "",
      "goal-in-num": "",
      "goal-in-name": "",

      //helper variables
      "part": 1,
      "roundtime": -1,
      "sincestart": -1,
      "clocktime": "00:00",
      "homeimage": "",
      "awayimage": "",
      "homecolor": "",
      "awaycolor": "",
      "hometextcolor": "",
      "awaytextcolor": "",
      "homegoals": "",
      "awaygoals": "",
      "homeredcards": 0,
      "awayredcards": 0,

      //computed
      "additionalTime": "",
      "isOvertime": false,
    },
    commands: {
      setHomeColor(color: string) {
        store.value.fields.homecolor = color
      },
      setHomeImage(image: string) {
        store.value.fields.homeimage = image
      },
      setHomeTextColor: (color: string) => {
        store.value.fields.hometextcolor = color
      },
      setAwayColor(color: string) {
        store.value.fields.awaycolor = color
      },
      setAwayImage(image: string) {
        store.value.fields.awayimage = image
      },
      setAwayTextColor: (color: string) => {
        store.value.fields.awaytextcolor = color
      },
      setPart: (part: number) => {
        if(store.value.fields.part != part) store.value.commands.hideOvertime()
        store.value.fields.part = part
      },
      setRoundTime: (time: number) => {
        store.value.fields.roundtime = time
      },
      setStartTimestamp: (time: number) => {
        store.value.fields.sincestart = time
      },
      displayOvertime: (amount: string) => {
        store.value.fields.additionalTime = amount
        timelines.overtime_timeline.tweenFromTo("start", "stop")
      },
      hideOvertime: () => {
        if(timelines.overtime_timeline.currentLabel() != "end")
          timelines.overtime_timeline.tweenFromTo("stop", "end")
      },
      goalHomeTeam: () => {
        if (state.value == "IN")
          timelines.goal_timeline.tweenFromTo("start", "end")
      },
      goalAwayTeam: () => {
        if (state.value == "IN")
          timelines.goal_timeline.tweenFromTo("start", "end")
      },
      substituteHomeAnimation: () => {
        if (state.value == "IN")
          timelines.substitute_timeline.tweenFromTo("start", "end")
      },
      substituteAwayAnimation: () => {
        if (state.value == "IN")
          timelines.substitute_timeline.tweenFromTo("start", "end")
      },
      displayReferee: () => {
        if (state.value == "IN")
          timelines.referee_timeline.tweenFromTo("start", "stop")
      },
      hideReferee: () => {
        timelines.referee_timeline.tweenFromTo("stop", "end")
      },
      goals: (csvgoals: string) => {
        const csv = csvgoals.split(",")
        store.value.fields.homegoals = csv[0]
        store.value.fields.awaygoals = csv[1]
      },
      redcards: (csvcards: string) => {
        const csv = csvcards.split(",")
        store.value.fields.homeredcards = parseInt(csv[0])
        store.value.fields.awayredcards = parseInt(csv[1])
      },
      addRedcard: (team: "home" | "away") => {
        store.value.fields[`${team}redcards`] += 1
        if(state.value == "IN") {
          timelines.redcard_timeline.tweenFromTo("start", "end", {onComplete: () => {
            const teamselector = team.charAt(0).toUpperCase() + "" + team.slice(1) as "Home" | "Away"
            const number = store.value.fields[`${team}redcards`] as 1 | 2 | 3
            timelines[`redcard${teamselector}${number}_timeline`].tweenFromTo("start", "stop")
          }})
        }
      },
      updateRedcard: (_: "home" | "away") => {
        if (state.value == "IN")
          timelines.redcard_timeline.tweenFromTo("start", "end")
      },
      addYellowcard: (_: "home" | "away") => {
        if (state.value == "IN")
          timelines.yellowcard_timeline.tweenFromTo("start", "end")
      },
      tick: () => {
        const sincestart = store.value.fields.sincestart
        const part = store.value.fields.part
        if(sincestart >= 0) {
          const now = Math.floor(Date.now() / 1000)
          const diff = now - sincestart

          const sec = diff % 60
          let min = Math.floor(diff / 60) + ((part-1) * ROUNDTIME)

          if(min >= part * ROUNDTIME && sec > 0) {
            if (!store.value.fields.isOvertime) store.value.fields.isOvertime = true
          } else {
            if (store.value.fields.isOvertime) store.value.fields.isOvertime = false
          }

          const minStr = min.toString().padStart(2, "0")
          const secStr = sec.toString().padStart(2, "0")

          store.value.fields.clocktime = `${minStr}:${secStr}`
        } else {
          if (store.value.fields.isOvertime) store.value.fields.isOvertime = false

          const min = (ROUNDTIME * (part-1)).toString().padStart(2,"0")
          const sec = "00"
          store.value.fields.clocktime = `${min}:${sec}`
        }
      }
    }

  })

  return store
}
