<script setup lang="ts">
import { onBeforeUnmount, onMounted, ref, h } from 'vue';
import gsap from '../shared/gsap'
import Draggable from 'gsap/Draggable';

window.gsap = gsap

let animationframe = -1
const timestep = ref<number>(0)
const playheadpos = ref<number>(0)

const playhead = ref<HTMLDivElement>()
const container = ref<HTMLDivElement>()


const timelines = ref<(gsap.core.Timeline)[]>(gsap.globalTimeline.getChildren(false, false, true) as gsap.core.Timeline[])
const firstTweens = ref<(gsap.core.Timeline | gsap.core.Tween)[]>([])

function refresh() {
  timelines.value = gsap.globalTimeline.getChildren(false, false, true) as gsap.core.Timeline[]
  firstTweens.value = timelines.value[0].getChildren()
  timestep.value = 1920 / timelines.value[0].duration()
  playheadpos.value = Math.floor(timelines.value[0].progress() * 1920)
  requestAnimationFrame(refresh)
}

let scrubber: Draggable[] = []

onMounted(() => {
  animationframe = requestAnimationFrame(refresh)

  if(playhead.value)
  scrubber = Draggable.create(playhead.value, {
    type: "x",
    bounds: document.getElementById("container"),
    inertia: false,
    onClick: () => {
      console.log("clicked");
    },
    onDrag: (e) => {
      timelines.value[0].progress(scrubber[0].x)
    },
    onDragStart: () => {
      console.log("drag started")
      gsap.globalTimeline.pause()
    },
    onDragEnd: () => {
      console.log("drag ended");
    }
  });
})

onBeforeUnmount(() => cancelAnimationFrame(animationframe))
</script>

<template>
  <div class="container" ref="container">
    <div v-for="(tween, id) in firstTweens" :key="id"
      :style="{
        background: 'green',
        marginLeft: `${tween.startTime() * timestep}px`,
        width: `${tween.duration() * timestep}px`
      }"
    >
      {{ tween.duration() }}
    </div>
    <div style="position: absolute; top: 0px; left: 1920px; width: 300px;">
      Step: {{ Math.floor(timestep) }}<br/>
      Playhead: {{ playheadpos }}
    </div>
    <div class="playhead"
      ref="playhead"
      :style="{left: `${playheadpos}px`}"
    ></div>
    <br>
  </div>
</template>

<style scoped lang="scss">
.container {
  color: var(--color-100);
  position: relative;
  left: 10px;
}

.playhead {
  position: absolute;
  top:0;
  width: 1px;
  height:100%;
