```ts nep-no-mlsnp-graphics/src/tools/shortcuts.ts
import { h, ref, Ref, watch } from "vue";
import { GFXLayers } from "../handlers/gfxcomponent";

export class ShortcutHotKeys {
  template: GFXLayers = [];
  activeIndex: Ref<number> = ref(0);
  toggle = false;

  constructor(template: GFXLayers) {
    this.template = template;
  }

  private updateTemplateNames() {
    for (let i = 0; i < this.template.length; i++) {
      const layer = this.template[i];
      if (layer.ref.value?.name) {
        layer.name = layer.ref.value?.name;
      } else {
        layer.name = "Unassigned";
      }
    }
  }

  private allIn() {
    this.template.map((element) => {
      if (element.ref.value?.state != "IN") {
        element.ref.value?.runAnimationIN();
      }
    });
  }
  private allOut() {
    this.template.map((element) => {
      if (element.ref.value?.state != "OUT") {
        element.ref.value?.runAnimationOUT();
      }
    });
  }

  public render() {
    this.updateTemplateNames();
    return h(
      "div",
      {
        style: `
        font-size: 16px;
        display: flex;
        flex-direction: column;
        position: absolute;
        color: white;
        top: 0px;
        left: 1930px;
        height: 1080px;
        overflow: visible scroll;
        padding-bottom: 540px;
      `,
      },
      [
        h(
          "div",
          {},
          `Active Layer: ${this.activeIndex.value + 1} (index: ${
            this.activeIndex.value
          })`
        ),
        h(
          "div",
          { style: `display: flex; justify-content: end; margin-right: 10px;` },
          h("div", [
            h(
              "button",
              {
                style: `width: 25px; margin-right: 0px; border: 1px solid #232323`,
                onClick: () => {
                  this.allOut();
                },
              },
              "O"
            ),
            h(
              "button",
              {
                style: `width: 25px; margin-right: 0px; border: 1px solid #232323`,
                onClick: () => {
                  this.allIn();
                },
              },
              "I"
            ),
          ])
        ),
        ...this.template.map((layer, i) => {
          return h(
            "div",
            {
              style: `
              flex-grow: 1;
              padding: 10px;
              margin-top: 10px;
              border: 1px solid ${this.activeIndex.value == i ? "red" : "#888"};
              position: relative;
            `,
              key: `layer-${layer.id}`,
            },
            layer.ref.value != null
              ? [
                  h("h3", `Layer: ${layer.name}`),
                  h("div", { style: "display: flex;" }, [
                    h(
                      "h4",
                      { style: "flex-grow: 1;" },
                      `State: ${layer.ref.value?.state}`
                    ),
                    h("div", [
                      h(
                        "button",
                        {
                          style: `${
                            layer.ref.value?.state == "OUT"
                              ? "background-color: orange; color: white;"
                              : ""
                          } width: 25px; margin-right: 0px; border: 1px solid #232323`,
                          onClick: () => {
                            layer.ref.value?.runAnimationOUT();
                          },
                        },
                        "O"
                      ),
                      h(
                        "button",
                        {
                          style: `${
                            layer.ref.value?.state == "IN"
                              ? "background-color: orange; color: white;"
                              : ""
                          } width: 25px; margin-right: 0px; border: 1px solid #232323`,
                          onClick: () => {
                            layer.ref.value?.runAnimationIN();
                          },
                        },
                        "I"
                      ),
                      h(
                        "button",
                        {
                          style: `${
                            layer.ref.value?.state == "NEXT"
                              ? "background-color: orange; color: white;"
                              : ""
                          } width: 25px; margin-right: 0px; border: 1px solid #232323`,
                          onClick: () => {
                            layer.ref.value?.runAnimationNEXT();
                          },
                        },
                        "N"
                      ),
                    ]),
                  ]),
                  h(
                    "h5",
                    {
                      style: `position: absolute; right: 2px; top: -2px; color:grey;`,
                    },
                    layer.id
                  ),
                ]
              : []
          );
        }),
      ]
    );
  }

  public start() {
    window.addEventListener("keydown", this.DevShortcuts.bind(this));
  }

  public stop() {
    window.removeEventListener("keydown", this.DevShortcuts.bind(this));
  }

  private DevShortcuts(ev: KeyboardEvent) {
    const layer = this.template[this.activeIndex.value].ref;
    switch (ev.key) {
      case "ArrowLeft":
      case "F1":
        if (
          this.activeIndex.value >= 0 &&
          this.activeIndex.value < this.template.length
        ) {
          if (layer && layer.value?.runAnimationOUT) {
            layer.value.runAnimationOUT();
          }
        }
        break;
      case "ArrowRight":
      case "F2":
        if (
          this.activeIndex.value >= 0 &&
          this.activeIndex.value < this.template.length
        ) {
          if (layer && layer.value?.runAnimationIN) {
            layer.value.runAnimationIN();
          }
        }
        break;
      case "F3":
        if (
          this.activeIndex.value >= 0 &&
          this.activeIndex.value < this.template.length
        ) {
          if (layer && layer.value?.runAnimationNEXT) {
            layer.value.runAnimationNEXT();
          }
        }
        break;
      case "ArrowUp":
        ev.preventDefault();
        this.prev();
        break;
      case "ArrowDown":
        ev.preventDefault();
        this.next();
        break;
    }
  }

  private prev() {
    this.activeIndex.value--;
    if (this.activeIndex.value <= -1)
      this.activeIndex.value = this.template.length - 1;
    if (this.template[this.activeIndex.value].ref.value == null) this.prev();
  }

  private next() {
    this.activeIndex.value++;
    if (this.activeIndex.value > this.template.length - 1)
      this.activeIndex.value = 0;
    if (this.template[this.activeIndex.value].ref.value == null) this.next();
  }
}
