function caps(text: string) {
  return `${text[0].toUpperCase()}${text.substring(1)}`
}

export default (key: keyof typeof languages, settings?: { caps?: boolean}) => {
  let lang = new URLSearchParams(window.location.search).get("lang") as SupportedLanguages
  if(!lang) {
    lang = "no"
  }

  if(!languages[key]) return "[Translation Missing]"
  if (!languages[key][lang]) return settings?.caps ? caps(languages[key].en) : languages[key].en
  return settings?.caps ? caps(languages[key][lang]) : languages[key][lang]
}
